import React,{useEffect,useState} from "react";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";
import CardMedia from '@mui/material/CardMedia';

// import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";

function Items() {

    const [products, setProducts] = useState([]);

    useEffect(() => {
      fetch('https://dummyjson.com/products')
        .then(response => response.json())
        .then(data => setProducts(data.products))
        .catch(error => console.error('Error fetching data:', error));
    }, []);

    console.log(products,'1234')

    const [cart, setCart] = useState([]);


    // const addToCart = product => {
    //   setCart(prevCart => [...prevCart, product]);
    // };


    const addToCart = product => {
      setCart(prevCart => [...prevCart, { ...product, quantity: 1 }]);
    };

    const decreaseQuantity = productId => {
      setCart(prevCart =>
        prevCart.map(item =>
          item.id === productId
            ? { ...item, quantity: Math.max(0, item.quantity - 1) }
            : item
        )
      );
    };

    const getProductInCart = productId => {
      return cart.find(item => item.id === productId);
    };
  return (
    <div className="App" style={{ background: "darkblu" }}>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static" style={{ background: "darkblue" }}>
          <Toolbar variant="dense">
            <IconButton
              edge="start"
              color="inherit"
              aria-label="menu"
              sx={{ mr: 2 }}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" color="inherit" component="div">
              Food's Restaurant
            </Typography>

            <Typography variant="h6" color="inherit" component="div">
            {cart.map(product => (
          <li key={product.id}>
            {product.name} - ${product.price}
          </li>
        ))}
            </Typography>

          </Toolbar>
        </AppBar>
      </Box>


      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
      {products.map(product => (
        <Card key={product.id} sx={{ minWidth: 275, margin: '10px', flex: '1' }}>
           <CardMedia
        sx={{ height: 140 }}
        image={product.images}
        title="green iguana"
      />
          <CardContent>
            <Box>{product.brand}</Box>
            <br />
            <Box>Price: {product.price}</Box>
          </CardContent>
          <CardActions>
            <Button onClick={() => addToCart(product)}>+</Button>

            <Button>-</Button>
            {getProductInCart(product.id) && (
              <button onClick={() => decreaseQuantity(product.id)}>Decrease</button>
            )}
          </CardActions>
        </Card>
      ))}
    </div>

      <Button variant="contained" style={{ background: "darkblue" }}>Go To Menu</Button>
    </div>
  );
}

export default Items;
