import React,{useEffect,useState} from "react";
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";
import CardMedia from '@mui/material/CardMedia';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';

// import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Modal from '@mui/material/Modal';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};


function Items() {

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
    const [products, setProducts] = useState([]);

    useEffect(() => {
      fetch('https://dummyjson.com/products')
        .then(response => response.json())
        .then(data => setProducts(data.products))
        .catch(error => console.error('Error fetching data:', error));
    }, []);

    console.log(products,'1234')

    const [cart, setCart] = useState([]);


    // const addToCart = product => {
    //   setCart(prevCart => {
    //     const existingProduct = prevCart.find(item => item.id === product.id);
    //     if (existingProduct) {
    //       existingProduct.quantity += 1;
    //       return [...prevCart];
    //     }
    //     return [...prevCart, { ...product, quantity: 1 }];
    //   });
    // };

    const updateCart = (product, action) => {
      setCart(prevCart => {
        const updatedCart = prevCart.map(item => {
          if (item.id === product.id) {
            if (action === "increase") {
              return { ...item, quantity: item.quantity + 1 };
            } else if (action === "decrease") {
              const newQuantity = Math.max(0, item.quantity - 1);
              if (newQuantity > 0) {
                return { ...item, quantity: newQuantity };
              }
            }
          }
          return item;
        });

        return updatedCart;
      });
    };


    const decreaseQuantity = productId => {
      setCart(prevCart =>
        prevCart.map(item =>
          item.id === productId
            ? { ...item, quantity: Math.max(0, item.quantity - 1) }
            : item
        )
      );
    };



  return (
    <div className="App" style={{ background: "darkblu" }}>



      <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" style={{ background: "darkblue" }}>
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" >
          Food's Restaurant
          </Typography>

          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>

          </Typography>
          <Button onClick={handleOpen}><AddShoppingCartIcon/></Button>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Box>Order Summary</Box>
          <Typography id="modal-modal-title" variant="h6" component="h2">
          {cart.map(product => (
                <li key={product.id}>
                  {product.brand} - {product.quantity}
                  <Button onClick={() => decreaseQuantity(product.id)}>-</Button>
                </li>
              ))}
          </Typography>
        </Box>
      </Modal>
        </Toolbar>
      </AppBar>
    </Box>

      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
      {products.map(product => (
        <Card key={product.id} sx={{ minWidth: 275, margin: '10px', flex: '1' }}>
           <CardMedia
        sx={{ height: 140 }}
        image={product.images}
        title="green iguana"
      />
          <CardContent>
            <Box>{product.brand}</Box>
            <br />
            <Box>Price: {product.price}</Box>
          </CardContent>
          <CardActions>
            {/* <Button onClick={() => addToCart(product)}>+</Button>

            <Button onClick={() => decreaseQuantity(product.id)}>-</Button>
            */}

<Button onClick={() => updateCart(product, "increase")}>+</Button>
  <Button onClick={() => updateCart(product, "decrease")}>-</Button>
          </CardActions>
        </Card>
      ))}
    </div>

      <Button variant="contained" style={{ background: "darkblue" }}>Go To Menu</Button>
    </div>
  );
}

export default Items;
